import React, { useState } from 'react';
import { Container, Row, Col, Card, Form, Button, Alert } from 'react-bootstrap';
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';
import 'bootstrap/dist/css/bootstrap.min.css';
import axios from 'axios';

function App() {
  const [formData, setFormData] = useState({
    inputValuePairs: [],
    barcodeDataList: [],
    iconInfoList: []
  });

  const [currentInput, setCurrentInput] = useState({
    input: '',
    value: '',
    fontFamily: 'Arial',
    fontSize: 12,
    xCoordinate: 0,
    yCoordinate: 0
  });

  const [currentBarcode, setCurrentBarcode] = useState({
    data: ''
  });

  const [loading, setLoading] = useState(false);

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setCurrentInput(prev => ({
      ...prev,
      [name]: name === 'fontSize' || name === 'xCoordinate' || name === 'yCoordinate' 
        ? parseInt(value) || 0 
        : value
    }));
  };

  const handleBarcodeChange = (e) => {
    const { name, value } = e.target;
    setCurrentBarcode(prev => ({
      ...prev,
      [name]: value
    }));
  };

  const addInputValuePair = () => {
    if (currentInput.input && currentInput.value) {
      setFormData(prev => ({
        ...prev,
        inputValuePairs: [...prev.inputValuePairs, { ...currentInput }]
      }));
      setCurrentInput({
        input: '',
        value: '',
        fontFamily: 'Arial',
        fontSize: 12,
        xCoordinate: 0,
        yCoordinate: 0
      });
      toast.success('Metin eklendi!');
    } else {
      toast.error('Lütfen input ve value alanlarını doldurun!');
    }
  };

  const addBarcodeData = () => {
    if (currentBarcode.data) {
      setFormData(prev => ({
        ...prev,
        barcodeDataList: [...prev.barcodeDataList, { 
          id: Date.now(), 
          data: currentBarcode.data 
        }]
      }));
      setCurrentBarcode({ data: '' });
      toast.success('Barkod eklendi!');
    } else {
      toast.error('Lütfen barkod verisini girin!');
    }
  };

  const removeInputValuePair = (index) => {
    setFormData(prev => ({
      ...prev,
      inputValuePairs: prev.inputValuePairs.filter((_, i) => i !== index)
    }));
    toast.info('Metin kaldırıldı!');
  };

  const removeBarcodeData = (index) => {
    setFormData(prev => ({
      ...prev,
      barcodeDataList: prev.barcodeDataList.filter((_, i) => i !== index)
    }));
    toast.info('Barkod kaldırıldı!');
  };

  const printLabels = async () => {
    setLoading(true);
    try {
      const response = await axios.post('/api/label/print', formData);
      toast.success('Etiketler başarıyla yazdırıldı!');
    } catch (error) {
      console.error('Print error:', error);
      toast.error(error.response?.data?.error || 'Yazdırma işlemi başarısız!');
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="App">
      <Container className="mt-4">
        <Row>
          <Col>
            <h1 className="text-center mb-4">TSC Printer Etiket Yazdırma</h1>
            
            {/* Metin Ekleme Bölümü */}
            <Card className="mb-4">
              <Card.Header>
                <h5>Metin Ekle</h5>
              </Card.Header>
              <Card.Body>
                <Row>
                  <Col md={6}>
                    <Form.Group className="mb-3">
                      <Form.Label>Input</Form.Label>
                      <Form.Control
                        type="text"
                        name="input"
                        value={currentInput.input}
                        onChange={handleInputChange}
                        placeholder="Input adı"
                      />
                    </Form.Group>
                  </Col>
                  <Col md={6}>
                    <Form.Group className="mb-3">
                      <Form.Label>Value</Form.Label>
                      <Form.Control
                        type="text"
                        name="value"
                        value={currentInput.value}
                        onChange={handleInputChange}
                        placeholder="Değer"
                      />
                    </Form.Group>
                  </Col>
                </Row>
                <Row>
                  <Col md={3}>
                    <Form.Group className="mb-3">
                      <Form.Label>Font Family</Form.Label>
                      <Form.Select
                        name="fontFamily"
                        value={currentInput.fontFamily}
                        onChange={handleInputChange}
                      >
                        <option value="Arial">Arial</option>
                        <option value="Times New Roman">Times New Roman</option>
                        <option value="Courier New">Courier New</option>
                      </Form.Select>
                    </Form.Group>
                  </Col>
                  <Col md={3}>
                    <Form.Group className="mb-3">
                      <Form.Label>Font Size</Form.Label>
                      <Form.Control
                        type="number"
                        name="fontSize"
                        value={currentInput.fontSize}
                        onChange={handleInputChange}
                        min="1"
                        max="72"
                      />
                    </Form.Group>
                  </Col>
                  <Col md={3}>
                    <Form.Group className="mb-3">
                      <Form.Label>X Koordinat</Form.Label>
                      <Form.Control
                        type="number"
                        name="xCoordinate"
                        value={currentInput.xCoordinate}
                        onChange={handleInputChange}
                        min="0"
                      />
                    </Form.Group>
                  </Col>
                  <Col md={3}>
                    <Form.Group className="mb-3">
                      <Form.Label>Y Koordinat</Form.Label>
                      <Form.Control
                        type="number"
                        name="yCoordinate"
                        value={currentInput.yCoordinate}
                        onChange={handleInputChange}
                        min="0"
                      />
                    </Form.Group>
                  </Col>
                </Row>
                <Button variant="primary" onClick={addInputValuePair}>
                  Metin Ekle
                </Button>
              </Card.Body>
            </Card>

            {/* Barkod Ekleme Bölümü */}
            <Card className="mb-4">
              <Card.Header>
                <h5>Barkod Ekle</h5>
              </Card.Header>
              <Card.Body>
                <Row>
                  <Col md={8}>
                    <Form.Group className="mb-3">
                      <Form.Label>Barkod Verisi</Form.Label>
                      <Form.Control
                        type="text"
                        name="data"
                        value={currentBarcode.data}
                        onChange={handleBarcodeChange}
                        placeholder="Barkod verisi"
                      />
                    </Form.Group>
                  </Col>
                  <Col md={4}>
                    <Button 
                      variant="primary" 
                      onClick={addBarcodeData}
                      className="mt-4"
                    >
                      Barkod Ekle
                    </Button>
                  </Col>
                </Row>
              </Card.Body>
            </Card>

            {/* Eklenen Metinler */}
            {formData.inputValuePairs.length > 0 && (
              <Card className="mb-4">
                <Card.Header>
                  <h5>Eklenen Metinler</h5>
                </Card.Header>
                <Card.Body>
                  {formData.inputValuePairs.map((item, index) => (
                    <Alert key={index} variant="info" className="d-flex justify-content-between align-items-center">
                      <span>
                        <strong>{item.input}:</strong> {item.value} 
                        (Font: {item.fontFamily}, Size: {item.fontSize}, 
                        Pos: {item.xCoordinate},{item.yCoordinate})
                      </span>
                      <Button 
                        variant="danger" 
                        size="sm"
                        onClick={() => removeInputValuePair(index)}
                      >
                        Kaldır
                      </Button>
                    </Alert>
                  ))}
                </Card.Body>
              </Card>
            )}

            {/* Eklenen Barkodlar */}
            {formData.barcodeDataList.length > 0 && (
              <Card className="mb-4">
                <Card.Header>
                  <h5>Eklenen Barkodlar</h5>
                </Card.Header>
                <Card.Body>
                  {formData.barcodeDataList.map((item, index) => (
                    <Alert key={index} variant="success" className="d-flex justify-content-between align-items-center">
                      <span>
                        <strong>Barkod {index + 1}:</strong> {item.data}
                      </span>
                      <Button 
                        variant="danger" 
                        size="sm"
                        onClick={() => removeBarcodeData(index)}
                      >
                        Kaldır
                      </Button>
                    </Alert>
                  ))}
                </Card.Body>
              </Card>
            )}

            {/* Yazdırma Butonu */}
            <Card>
              <Card.Body className="text-center">
                <Button 
                  variant="success" 
                  size="lg"
                  onClick={printLabels}
                  disabled={loading}
                >
                  {loading ? 'Yazdırılıyor...' : 'Etiketleri Yazdır'}
                </Button>
              </Card.Body>
            </Card>
          </Col>
        </Row>
      </Container>
      
      <ToastContainer 
        position="top-right"
        autoClose={3000}
        hideProgressBar={false}
        newestOnTop={false}
        closeOnClick
        rtl={false}
        pauseOnFocusLoss
        draggable
        pauseOnHover
      />
    </div>
  );
}

export default App;
